import { useState, useEffect } from 'react';
import Timeline from './components/Timeline';
import CharacterCard from './components/CharacterCard';
import { bibleCharacters as rawCharacters } from './data/bibleCharacters';
import { quizzes } from './data/quizzes';

// ÌÉúÍ∑∏ Ï∂îÍ∞Ä ÏòàÏãú (Ïã§Ï†ú Îç∞Ïù¥ÌÑ∞Ïóê ÎßûÍ≤å Ï°∞Ï†ï)
const bibleCharacters = rawCharacters.map(char => ({
    ...char,
    tags: char.tags || (
        char.id === 'adam' ? ['Ï∞ΩÏÑ∏Í∏∞', 'Ï≤´ Ïù∏Í∞Ñ', 'ÏóêÎç¥ÎèôÏÇ∞'] :
            char.id === 'noah' ? ['Î∞©Ï£º', 'ÎåÄÌôçÏàò', 'ÎØøÏùå'] :
                char.id === 'abraham' ? ['ÎØøÏùå', 'ÏïΩÏÜç', 'Ïù¥ÏÇ≠Ïùò ÏïÑÎ≤ÑÏßÄ'] :
                    char.id === 'moses' ? ['Ï∂úÏï†ÍµΩ', 'Ïã≠Í≥ÑÎ™Ö', 'ÏßÄÎèÑÏûê'] :
                        char.id === 'david' ? ['Ïôï', 'Í≥®Î¶¨Ïïó', 'ÏãúÌé∏'] :
                            char.id === 'daniel' ? ['Ìè¨Î°ú', 'ÏÇ¨ÏûêÍµ¥', 'Ïã†Ïïô'] : []
    )
}));

export default function App() {
    const [selectedId, setSelectedId] = useState(bibleCharacters[0].id);
    const selectedChar = bibleCharacters.find(c => c.id === selectedId);
    const [quizStep, setQuizStep] = useState(0);
    const [quizResult, setQuizResult] = useState(null);

    useEffect(() => {
        setQuizStep(0);
        setQuizResult(null);
    }, [selectedId]);

    const quizList = quizzes[selectedId];
    const quiz = quizList[quizStep];

    function handleAnswer(idx) {
        if (idx === quiz.answer) setQuizResult('Ï†ïÎãµÏûÖÎãàÎã§!');
        else setQuizResult('ÌãÄÎ†∏Ïñ¥Ïöî. Îã§Ïãú ÏãúÎèÑÌï¥Î≥¥ÏÑ∏Ïöî.');
    }

    function handleNext() {
        setQuizResult(null);
        if (quizStep < quizList.length - 1) setQuizStep(quizStep + 1);
        else setQuizStep(0); // ÎßàÏßÄÎßâ Î¨∏Ï†ú ÌõÑ Ï≤òÏùåÏúºÎ°ú
    }

    return (
        <div style={{ minHeight: '100vh', background: 'linear-gradient(180deg, #b71c1c 0%, #2a2734 100%)', padding: 0 }}>
            <div style={{ maxWidth: 1200, margin: '0 auto', padding: '32px 24px 0 24px' }}>
                <h1 style={{ color: '#fff', textAlign: 'center', fontWeight: 700, fontSize: '2.8rem', marginBottom: 8 }}>ÏÑ±Í≤Ω ÏãúÍ∞ÑÏÑ† ÌÉêÌóò</h1>
                <p style={{ color: '#fff', textAlign: 'center', fontSize: '1.2rem', marginBottom: 32 }}>ÏÑ±Í≤ΩÏùò Ï£ºÏöî Ïù∏Î¨ºÎì§ÏùÑ ÏãúÍ∞ÑÏàúÏúºÎ°ú ÎßåÎÇòÎ≥¥ÏÑ∏Ïöî</p>
                <Timeline characters={bibleCharacters} selectedId={selectedId} onSelect={setSelectedId} />
                <div style={{ background: '#181624', borderRadius: 24, padding: 32, margin: '32px 0', minHeight: 320, display: 'flex', alignItems: 'center', justifyContent: 'center', flexDirection: 'column' }}>
                    <div style={{ marginBottom: 8, fontWeight: 'bold', color: '#b71c1c', fontSize: 18 }}>
                        ÎÇúÏù¥ÎèÑ: {quiz.level}
                    </div>
                    <div style={{ marginBottom: 8, color: '#888', fontSize: 15 }}>
                        üìñ {quiz.verse}
                    </div>
                    <h3 style={{ margin: '12px 0', color: '#fff' }}>{quiz.question}</h3>
                    {quiz.options.map((opt, idx) => (
                        <button
                            key={idx}
                            onClick={() => handleAnswer(idx)}
                            style={{
                                display: 'block', margin: '8px 0', width: 320,
                                background: '#f0f4fa', border: '1px solid #ddd', borderRadius: 8, padding: 10, fontSize: 16
                            }}
                            disabled={!!quizResult}
                        >
                            {opt}
                        </button>
                    ))}
                    {quizResult && (
                        <div style={{ margin: '16px 0', color: quizResult === 'Ï†ïÎãµÏûÖÎãàÎã§!' ? '#388e3c' : '#d32f2f', fontWeight: 'bold' }}>
                            {quizResult}
                        </div>
                    )}
                    <div style={{ marginTop: 8, color: '#b71c1c', fontSize: 14 }}>
                        üìñ {quiz.verse}
                    </div>
                    <div style={{ marginTop: 16 }}>
                        {quizResult && (
                            <button onClick={handleNext} style={{
                                background: '#b71c1c', color: '#fff', border: 'none', borderRadius: 8, padding: '8px 20px', fontWeight: 'bold', marginRight: 8
                            }}>
                                {quizStep < quizList.length - 1 ? 'Îã§Ïùå Î¨∏Ï†ú' : 'Ï≤òÏùåÎ∂ÄÌÑ∞'}
                            </button>
                        )}
                    </div>
                </div>
                <CharacterCard character={selectedChar} />
            </div>
        </div>
    );
}
